import React, { Component } from 'react';


  
export default class D6 extends Component{
      
      
    constructor(props){
        super(props);
        this.state = {D1:""};
        this.rollD6 = this.rollD6.bind(this);
        
       
        
      }
    
   
   
   
    rollD6(RollCount, MyArr){
        if(this.RollCount < 4){
            console.log(rollCount);
           // this.MyArr[0] = Math.floor((Math.random()*6)+1);
            this.setState({D1: Math.floor((Math.random()*6)+ 1)});
        }
        this.RollCount++;
       // this.setState({rollTotal})
   }
   
    render(){
        let rollCount = 0;
        let MyArr = [0, 0, 0, 0];
       return(
     <div>
    <button className="btn" type="button" onClick={(rollCount, myArr) => this.rollD6()}> 
    Roll D6 
    </button>
    <textarea className="form-control" value={this.state.D1} aria-label="With textarea"></textarea>
    </div>
    );
}
}
