import React, { Component } from 'react';

  
export default class D6 extends Component{
      RollCount = 1;
      RollTotal = 0;
      FirstRoll = 0;
      SecondRoll = 0;
      ThirdRoll = 0;
      FourthRoll = 0;
      My
    constructor(props){
        super(props);
        this.state = {D1:""};
        this.rollD6 = this.rollD6.bind(this);
        
       
        
      }
    
   
   
   
    rollD6(){
        this.RollCount++;
        this.setState({D1: Math.floor((Math.random()*6)+ 1)});
        if(this.RollCount === 1){
            this.FirstRoll =  Math.floor((Math.random()*6)+ 1);
        }
        if(this.RollCount === 2){
            this.SecondRoll = Math.floor((Math))
        }
       // this.setState({rollTotal})
   }
   
    render(){
       return(
     <div>
    <button className="btn" type="button" onClick={() => this.rollD6()}> 
    Roll D6 
    </button>
    <textarea className="form-control" value={this.state.D1} aria-label="With textarea"></textarea>
    </div>
    );
}
}
