import React, { Component } from 'react';

export default class StatBox extends Component {
  
  hydrateStateWithLocalStorage(){
    for(let key in this.state){
         if(localStorage.hasOwnProperty(key)){
             let value = localStorage.getItem(key);

             try{
                 value = JSON.parse(value);
                 this.setState({[key]:value});
             }
             catch(e){
                 this.setState({[key]:value});
             }
         }
    }
}



 componentDidMount(){
     this.hydrateStateWithLocalStorage();
     window.addEventListener(
         "beforeunload", this.saveStateToLocalStorage.bind(this)
     );
 }
 
 componentWillUnmount(){
     window.removeEventListener(
         "beforeunload", this.saveStateToLocalStorage.bind(this)
     );
     this.saveStateToLocalStorage();
 }
 
 updateInput(key, value){
     this.setState({[key]: value});
     localStorage.setItem(key,value);
 }

 saveStateToLocalStorage(){
     for(let key in this.state){
         localStorage.setItem(key, JSON.stringify(this.state[key]));
     }
 }
  handleChange(event){
    var StatName = this.props.State;
    this.setState({StatName: event.target.value})
  }
    render() {
        return (
            <div className="btn-toolbar mb-3" role="toolbar" aria-label="Toolbar with button groups">
            <div className="input-group">
              <div className="input-group-prepend">
                <div className="input-group-text" id="btnGroupAddon">{this.props.Stat}</div>
              </div>
              <div className="col-2">
              <input type="text" name = {this.props.Stat} onChange={this.handleChange.bind(this)} className="form-control"/>
              </div>
              <button type="button" className="btn btn-secondary">+</button>
            </div>
          </div>
          
        );
    }
}
