import React, { Component } from 'react';

export default class D6 extends Component{
    constructor(props){
        super(props);
        var rollCount = 0;
        this.state = {D1:""};
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmiut = this.handleSubmiut.bind(this);
        this.rollD6 = this.rollD6.bind(this);
      }
      
      handleChange(event){
        this.rollD6();
      }
    
      handleSubmiut(event){
        event.preventDefault();
      }
   
   
    rollD6(){
        
        this.setState({D1: Math.floor((Math.random()*6)+ 1)});
   }
   
    render(){
       return(
     <div>
    <button className="btn" type="button" onClick={() => this.rollD6()}> 
    Roll D6 
    </button>
    <textarea className="form-control" value={this.state.D1} aria-label="With textarea"></textarea>
    </div>
    );
}
}
