import React, { Component } from 'react';

export default class StatBox extends Component {
  
  handleChange(event){
    var StatName = this.props.State;
    this.setState({StatName: event.target.value})
  }
    render() {
        return (
            <div className="btn-toolbar mb-3" role="toolbar" aria-label="Toolbar with button groups">
            <div className="input-group">
              <div className="input-group-prepend">
                <div className="input-group-text" id="btnGroupAddon">{this.props.Stat}</div>
              </div>
              <div className="col-2">
              <input type="text" name = {this.props.Stat} value={this.state.StatName} onChange={this.handleChange.bind(this)} className="form-control"/>
              </div>
              <button type="button" className="btn btn-secondary">+</button>
            </div>
          </div>
          
        );
    }
}
